import React, { useState, useRef } from 'react';
import {
    IconButton, Stack, Grid, Button, Paper, TextareaAutosize, Divider
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

import { toast } from 'react-toastify';
import { uploadFiles } from '../services/file.service';


export function Converter() {
    const ref = useRef();
    const [file, setFile] = useState(null);
    const [fileContent, setFileContent] = useState("");
    const [buttonText, changeText] = useState("Copy");

    const handleFileSelect = (event) => {
        setFile(event.target.files);
    }

    const reset = () => {
        ref.current.value = "";
        setFile(null);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        changeText("Copy")
        const formData = new FormData();
        formData.append("file", file[0]);
        formData.append("fileName", file[0].name);
        uploadFiles(formData)
            .then((response) => {
                if (response.data.status) {
                    setFileContent(response.data.data);
                    setFile("");
                    reset();
                } else {
                    toast(response.data.message, {
                        position: "top-right",
                        autoClose: 3000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        type: "error"
                    });
                }
            }).catch((error) => {
                toast(error.message, {
                    position: "top-right",
                    autoClose: 3000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    type: "error"
                });
            });
    }

    return (
        <Paper variant="outlined" sx={{ my: { xs: 3, md: 6 }, p: { xs: 2, md: 3 } }}>
            <Grid container spacing={2} columns={16}>
                <Grid item xs={7}>
                    <Paper variant="outlined" sx={{ height: 60, width: 444 }}>
                        <Stack direction="row" alignItems="center" spacing={2} justifyContent="center" sx={{ marginTop: 1 }}>
                            <label htmlFor="contained-button-file">
                                <input accept="application/pdf" id="contained-button-file" type="file"
                                    onChange={handleFileSelect} name="file" ref={ref} />
                            </label>
                            <IconButton aria-label="delete" onClick={reset}>
                                <DeleteIcon />
                            </IconButton>
                            <Button variant="contained"
                                sx={{ justifyContent: 'flex-end', marginLeft: 2 }}
                                color="success"
                                disabled={!file}
                                onClick={handleSubmit}>Convert</Button>
                        </Stack>
                    </Paper>
                </Grid>
                <Divider orientation="vertical" flexItem />
                <Grid item xs={8}>
                    <TextareaAutosize
                        placeholder="Extracted text from the PDF..."
                        style={{ width: 620, height: 400, overflow: 'scroll' }}
                        value={fileContent}
                    />
                    <Stack
                        direction="row"
                        spacing={50}
                    >
                        <Button variant="outlined"
                            disabled={!fileContent}
                            color={buttonText === "Copy" ? "primary" : "success"}
                            onClick={() => { navigator.clipboard.writeText(fileContent); changeText("Copied") }}
                        >{buttonText}</Button>
                        <Button variant="outlined"
                            disabled={!fileContent}
                            onClick={() => { setFileContent(""); changeText("Copy") }}
                        >Clear</Button>
                    </Stack>

                </Grid>
            </Grid>
        </Paper>
    );
}

export default Converter;
